  calculaTiempo(date1: Date, date2: Date): string {

    const anio1 = date1.getFullYear();
    const mes1 = date1.getMonth();
    const dia1 = date1.getDate();
    const hora1 = date1.getHours();
    const minuto1 = date1.getMinutes();
    const segundo1 = date1.getSeconds();

    const anio2 = date2.getFullYear();
    const mes2 = date2.getMonth();
    const dia2 = date2.getDate();
    const hora2 = date2.getHours();
    const minuto2 = date2.getMinutes();
    const segundo2 = date2.getSeconds();

    let diffAnio = anio2 - anio1;
    let diffMes = mes2 - mes1;
    let diffDia = dia2 - dia1;
    let diffHora = hora2 - hora1;
    let diffMinuto = minuto2 - minuto1;
    let diffSegundo = segundo2 - segundo1;

    // const diasMes = [31,28,31,30,31,30,31,31,30,31,30,31]
    
    let tiempo = ''

    if (diffHora < 0) {
      diffDia -= 1;
      diffHora = 24 - Math.abs(diffHora);
    }
    if (diffMinuto < 0) {
      diffHora -= 1;
      diffMinuto = 60 - Math.abs(diffMinuto);
    }
    if (diffDia > 0) {
      tiempo += diffDia;
      if (diffDia> 1) {
        tiempo += ' days'
      }
      else { tiempo += ' day'}
    }

    if (diffHora > 0) {
      tiempo += ' ' + diffHora;
      if (diffHora > 1) {
        tiempo += ' hours'
      }
      else { tiempo += ' hour'}
    }

    if (diffDia === 0 && Math.abs(diffMinuto) > 0) {
      tiempo += ' ' + Math.abs(diffMinuto);
      if (diffMinuto > 1) {
        tiempo += ' minutes'
      }
      else { tiempo += ' minute'}
    } 
    
    if (diffDia === 0 && diffHora === 0 && diffSegundo > 0) {
      tiempo += ' ' + diffSegundo;
      if (diffSegundo > 1) {
        tiempo += ' seconds'
      }
      else { tiempo += ' second'}
    }

    return tiempo
  }

